function craftFinnishNames(set_name) {
    
    new_finnish_names = finnish_names.craftMultipleNames(4, [{set:set_name},{set:"last_names"}]).concat(finnish_names.craftMultipleNames(3, [{set:set_name},{set:set_name},{set:"last_names"}]),finnish_names.craftMultipleNames(3, [{set:set_name},{set:set_name},{set:set_name},{set:"last_names"}]));
    
    let name_list = [];
    
    for (let i = 0; i < new_finnish_names.length; i++) {
        
        let name = "";
        let full_name = new_finnish_names[i];
        for (let s = 0; s < full_name.length; s++) {
            name += " " + full_name[s].name;
        }
        
        name_list.push(name);
           
    }
    
    document.getElementById("finnish_names").innerHTML = "<p><b>" + name_list.join("</b></p><p><b>").trim() + "</b></p>";
    
}

function craftDrowNames(set_name) {
    
    new_drow_names = drow_names.craftMultipleNames(5, [{set:set_name},{set:"house"}]);
    
    let current_list = drow[set_name];
    
    let name_list = [];
    
    for (let i = 0; i < new_drow_names.length; i++) {
        
        let name = [];
        let meaning = [];
        let full_name = new_drow_names[i];
        let listing;
        
        for (let n = 0; n < full_name.length; n++) {
            
            if (n < full_name.length - 1) {
                listing = current_list;
                meaning.push(" ");
            } else { 
                listing = drow.house;
                meaning.push(",");
            }
            
            let parts = full_name[n].originalSyllables;
            let meaning_list;
            let name_wrapped = "";
            let pre = false;
            
            
            for (let s = 0; s < parts.length; s++) {
                
                if (parts[s]) {
                    name_wrapped += parts[s] == house_preposition || parts[s] == "'" ? parts[s] : "<span class='name_part'>" + parts[s] + "</span>";

                    if (s == 0 || pre) {
                        meaning_list = listing.prefix.meanings;
                        pre = false;
                    } else {
                        meaning_list = s == parts.length-1 ? listing.suffix.meanings : listing.middle.meanings;
                    }
                    
                    if (meaning_list[parts[s]]) {
                        meaning.push("<span class='name_part'>" + meaning_list[parts[s]] + "</span>");
                    } else if (parts[s] == house_preposition) {
                        meaning.push(house_preposition + ":");
                        pre = true;
                        
                    } else if (parts[s] != "'") {
                        meaning.push("<span class='name_part'>-</span>");
                    }
                }
            }
            
            name.push(name_wrapped);
            
        }
        
        name = "<p class='name_data'><b class='fullname'>" + name.join(" ").trim() + "</b><br/><small class='meaning'>'" + meaning.join(" ").trim() + "'</small></p>";
        
        name_list.push(name);
           
    }
    
    document.getElementById("drow_names").innerHTML = name_list.join("");
    
}